mapper.select:
SELECT FTC_PF_TRAN_ID FROM BENEFICIOS.TTCRXGRAL_PS_PF_DEVD_HDRCIF
AND FTC_FOLIO_REL = #{folio,jdbcType=VARCHAR}
AND FTC_TIMBRADO_CIF = '0'

SELECT FTC_PF_TRAN_ID FROM BENEFICIOS.TTCRXGRAL_PS_PF_APV_HDRCIF
AND FTC_FOLIO_REL = #{folio,jdbcType=VARCHAR}
AND FTC_TIMBRADO_CIF = '0'

SELECT 'APORTACIONES VOLUNTARIAS' AS NOMBRE_PROCESO, TCDAT.FTC_SUBPROCESO_CIF AS CVE_SUB_PROCESO, (CASE WHEN TCDAT.FTC_SUBPROCESO_CIF='DEV-PROSPECTO' THEN 'DEVOLUCIONES DE PROSPECTOS' WHEN TCDAT.FTC_SUBPROCESO_CIF='DEV-CLIENTE' THEN 'DEVOLUCIÃ“N DE CLIENTES' WHEN TCDAT.FTC_SUBPROCESO_CIF='DEV-PROFU-PROSPEC' THEN 'DEVOLUCIONES DE CONTRACARGO PROSPECTOS' WHEN TCDAT.FTC_SUBPROCESO_CIF='DEV-PROCESAR' THEN 'DEVOLUCIONES DE CONTRA CARGOS PROCESAR' WHEN TCDAT.FTC_SUBPROCESO_CIF='DEV-PROFUTURO' THEN 'DEVOLUCIONES DE CONTRA CARGOS PROFUTURO' END) AS NOMBRE_SUB_PROCESO, SUM(TCDAT.FTN_CTAS_INDV_OPERA) AS CASOS_OPERADOS, SUM(TCDAT.FTN_IMP_PESOS) AS IMPORTE_PESOS FROM TDAFOAVOL_CIF_DEV_APV_TOT TCDAT
AND FTC_FOLIO = #{folio,jdbcType=VARCHAR}
GROUP BY FTC_SUBPROCESO_CIF

SELECT FTC_PF_TRAN_ID AS TRANSACCION_ID,
       FTC_PF_STATUS_CIF AS STATUS_CIF
FROM BENEFICIOS.TTCRXGRAL_PS_PF_DEVD_HDRCIF	


  			AND FTC_FOLIO_REL = #{folio,jdbcType=VARCHAR}


  		AND FTC_PF_STATUS_CIF IN

  		 #{estatusCif,jdbcType=VARCHAR}

SELECT FTC_PF_TRAN_ID AS TRANSACCION_ID,
       FTC_PF_STATUS_CIF AS STATUS_CIF 
FROM BENEFICIOS.TTCRXGRAL_PS_PF_APV_HDRCIF	


  			AND FTC_FOLIO_REL = #{folio,jdbcType=VARCHAR}



  			AND FTC_PF_STATUS_CIF IN

  		 #{estatusCif,jdbcType=VARCHAR}
