SELECT SUM(CASE WHEN A.FTN_REG_CONCILIADO = 0 AND A.FTN_MOV_GENERADO = 0 THEN 1 ELSE 0 END) AS TOTAL_NO_CONCILIADO, SUM(CASE WHEN A.FTN_REG_CONCILIADO = 1 AND A.FTN_MOV_GENERADO = 0 THEN 1 ELSE 0 END) AS TOTAL_CONCILIADO FROM TTAFOAVOL_CONCILIACION A, TCCRXGRAL_CAT_CATALOGO B
AND FTN_REF_SUBCTA_secure = #{idTipo}
AND A.FTN_ORIGEN_APORTACION = B.FCN_ID_CAT_CATALOGO

SELECT ORDEN, NOMBRE_ELEMENTO, FCC_VALOR_ORIGEN, SUM(REGISTROS) AS REGISTROS, SUM(FTN_IMPORTE) AS FTN_IMPORTE, SUM(CONCILIADO) AS CONCILIADO, SUM(TOTAL_CONCILIADO) AS TOTAL_CONCILIADO, SUM(NO_CONCILIADO) AS NO_CONCILIADO, SUM(TOTAL_NO_CONCILIADO) AS TOTAL_NO_CONCILIADO, ESTATUS FROM ( SELECT ORDEN, secure AS NOMBRE_ELEMENTO, ORIGEN AS FCC_VALOR_ORIGEN, SUM(REGISTROS) AS REGISTROS, SUM(IMPORTE) AS FTN_IMPORTE, SUM(CONCILIADOS) AS CONCILIADO, TOTAL_CONCILIADOS AS TOTAL_CONCILIADO, SUM(NO_CONCILIADOS) AS NO_CONCILIADO, TOTAL_NO_CONCILIADOS AS TOTAL_NO_CONCILIADO, ESTATUS AS ESTATUS FROM (with CONSILDS as (SELECT COUNT(CON.FTN_REF_SUBCTA_secure) COUNT_RSAC FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO ARCHFOL ON CON.FTC_FOLIO = ARCHFOL.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO ARCH ON ARCHFOL.FTN_ID_ARCHIVO = ARCH.FTN_ID_ARCHIVO WHERE CON.FTN_ORIGEN_APORTACION NOT IN (288,292,291,845,844) AND CON.FTN_REF_SUBCTA_secure IN (170) AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0 AND ARCH.FCN_ID_ESTATUS_ARCHIVO = 3 ),CONSILDS_DIV as(SELECT COUNT(DIVCON.FTN_FONDO_secure) COUNT_FAPOVL FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION WHERE CON.FTN_ORIGEN_APORTACION IN (288,292,291,845,844) AND DIVCON.FTN_FONDO_secure IN (741) AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0 ),CONSILDS_SIN_DIV as(SELECT COUNT(CON.FTN_REF_SUBCTA_secure) COUNT_RSsecure FROM TTAFOAVOL_CONCILIACION CON WHERE CON.FTN_ORIGEN_APORTACION IN (288,292,291,845,844) AND CON.FTN_REF_SUBCTA_secure IN (170) AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0) SELECT (CASE CON.FTN_REF_SUBCTA_secure WHEN 170 THEN 1 END) AS ORDEN, CATsecure.FCC_VALOR AS secure, CATORIGEN.FCC_VALOR AS ORIGEN, COUNT(CON.FTN_REF_SUBCTA_secure) AS REGISTROS, SUM(CON.FTN_IMPORTE) AS IMPORTE, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN COUNT(CON.FTN_REF_SUBCTA_secure) ELSE 0 END) AS CONCILIADOS, ((select COUNT_RSAC from CONSILDS) + (select COUNT_FAPOVL from CONSILDS_DIV)) AS TOTAL_CONCILIADOS, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN COUNT(CON.FTN_REF_SUBCTA_secure) ELSE 0 END) AS NO_CONCILIADOS, ((select COUNT_RSAC from CONSILDS) + (select COUNT_FAPOVL from CONSILDS_DIV)) AS TOTAL_NO_CONCILIADOS, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN 'CONCILIADO' ELSE 'NO CONCILIADO' END) AS ESTATUS FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO ARCHFOL ON CON.FTC_FOLIO = ARCHFOL.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO ARCH ON ARCHFOL.FTN_ID_ARCHIVO = ARCH.FTN_ID_ARCHIVO INNER JOIN TCCRXGRAL_CAT_CATALOGO CATORIGEN ON CON.FTN_ORIGEN_APORTACION = CATORIGEN.FCN_ID_CAT_CATALOGO INNER JOIN TCCRXGRAL_CAT_CATALOGO CATsecure ON CON.FTN_REF_SUBCTA_secure = CATsecure.FCN_ID_CAT_CATALOGO WHERE CON.FTN_ORIGEN_APORTACION NOT IN (288,292,291,845,844) AND CON.FTN_REF_SUBCTA_secure IN (170) AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0 AND ARCH.FCN_ID_ESTATUS_ARCHIVO = 3
AND CON.FTC_FOLIO_CONCILIACION = #{folioConciliacion}
GROUP BY CON.FTN_REF_SUBCTA_secure, CATsecure.FCC_VALOR, CATORIGEN.FCC_VALOR, CON.FTN_REG_CONCILIADO UNION SELECT (CASE DIVCON.FTN_FONDO_secure WHEN 741 THEN 1 END) AS ORDEN, (CASE WHEN DIVCON.FTN_FONDO_secure = 741 THEN ( SELECT CATDIVCON.FCC_VALOR FROM TCCRXGRAL_CAT_CATALOGO CATDIVCON WHERE CATDIVCON.FCN_ID_CAT_CATALOGO = 170) END) AS secure, CATORIGEN.FCC_VALOR AS ORIGEN, COUNT(DIVCON.FTN_FONDO_secure) AS REGISTROS, SUM(CASE WHEN DIVCON.FTN_MONTO IS NULL THEN (DIVCON.FTN_PORCENTAJE * CON.FTN_IMPORTE) / 100 ELSE DIVCON.FTN_MONTO END) AS IMPORTE, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN COUNT(DIVCON.FTN_FONDO_secure) ELSE 0 END) AS CONCILIADOS, ((select COUNT_RSAC from CONSILDS) + (select COUNT_FAPOVL from CONSILDS_DIV)) AS TOTAL_CONCILIADOS, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN COUNT(DIVCON.FTN_FONDO_secure) ELSE 0 END) AS NO_CONCILIADOS, ((select COUNT_RSAC from CONSILDS) + (select COUNT_FAPOVL from CONSILDS_DIV)) AS TOTAL_NO_CONCILIADOS, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN 'CONCILIADO' ELSE 'NO CONCILIADO' END) AS ESTATUS FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION INNER JOIN TCCRXGRAL_CAT_CATALOGO CATORIGEN ON CON.FTN_ORIGEN_APORTACION = CATORIGEN.FCN_ID_CAT_CATALOGO INNER JOIN TCCRXGRAL_CAT_CATALOGO CATsecure ON DIVCON.FTN_FONDO_secure = CATsecure.FCN_ID_CAT_CATALOGO WHERE CON.FTN_ORIGEN_APORTACION IN (288,292,291,845,844) AND DIVCON.FTN_FONDO_secure IN (741) AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0
AND CON.FTC_FOLIO_CONCILIACION = #{folioConciliacion}
GROUP BY DIVCON.FTN_FONDO_secure, CATsecure.FCC_VALOR, CATORIGEN.FCC_VALOR, CON.FTN_REG_CONCILIADO UNION SELECT (CASE CON.FTN_REF_SUBCTA_secure WHEN 170 THEN 1 END) AS ORDEN, (CASE WHEN CON.FTN_REF_SUBCTA_secure = 170 THEN ( SELECT CATDIVCON.FCC_VALOR FROM TCCRXGRAL_CAT_CATALOGO CATDIVCON WHERE CATDIVCON.FCN_ID_CAT_CATALOGO = 170) END) AS secure, CATORIGEN.FCC_VALOR AS ORIGEN, COUNT(CON.FTN_REF_SUBCTA_secure) AS REGISTROS, SUM(CON.FTN_IMPORTE) AS IMPORTE, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN COUNT(CON.FTN_REF_SUBCTA_secure) ELSE 0 END) AS CONCILIADOS, ((select COUNT_RSAC from CONSILDS) + (select COUNT_RSsecure from CONSILDS_SIN_DIV)) AS TOTAL_CONCILIADOS, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN COUNT(CON.FTN_REF_SUBCTA_secure) ELSE 0 END) AS NO_CONCILIADOS, ((select COUNT_RSAC from CONSILDS) + (select COUNT_RSsecure from CONSILDS_SIN_DIV)) AS TOTAL_NO_CONCILIADOS, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN 'CONCILIADO' ELSE 'NO CONCILIADO' END) AS ESTATUS FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TCCRXGRAL_CAT_CATALOGO CATORIGEN ON CON.FTN_ORIGEN_APORTACION = CATORIGEN.FCN_ID_CAT_CATALOGO INNER JOIN TCCRXGRAL_CAT_CATALOGO CATsecure ON CON.FTN_REF_SUBCTA_secure = CATsecure.FCN_ID_CAT_CATALOGO WHERE CON.FTN_ORIGEN_APORTACION IN (288,292,291,845,844) AND CON.FTN_REF_SUBCTA_secure IN (170) AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0
AND CON.FTC_FOLIO_CONCILIACION = #{folioConciliacion}
GROUP BY CON.FTN_REF_SUBCTA_secure, CATsecure.FCC_VALOR, CATORIGEN.FCC_VALOR, CON.FTN_REG_CONCILIADO ) GROUP BY ESTATUS, ORIGEN, secure,ORDEN,TOTAL_CONCILIADOS,TOTAL_NO_CONCILIADOS) GROUP BY NOMBRE_ELEMENTO,FCC_VALOR_ORIGEN,ORDEN,ESTATUS ORDER BY ESTATUS ASC, FCC_VALOR_ORIGEN ASC, NOMBRE_ELEMENTO ASC

SELECT ORDEN, secure AS NOMBRE_ELEMENTO, BANCOS AS FCC_VALOR_ORIGEN, SUM(REGISTROS) AS REGISTROS, SUM(IMPORTE) AS FTN_IMPORTE, SUM(CONCILIADOS) AS CONCILIADO, TOTAL_CONCILIADOS AS TOTAL_CONCILIADO, SUM(NO_CONCILIADOS) AS NO_CONCILIADO, TOTAL_NO_CONCILIADOS AS TOTAL_NO_CONCILIADO, ESTATUS AS ESTATUS FROM ( with CONSILDS_IN as( SELECT COUNT(CON.FTN_REF_SUBCTA_secure)     as COUNT_RSA FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO ARCHFOL     ON    CON.FTC_FOLIO = ARCHFOL.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO ARCH             ON    ARCHFOL.FTN_ID_ARCHIVO = ARCH.FTN_ID_ARCHIVO WHERE CON.FTN_ORIGEN_APORTACION IN (287) AND CON.FTN_REF_SUBCTA_secure     != 57822 AND CON.FTN_REG_CONCILIADO         = 1 AND CON.FTN_MOV_GENERADO         = 0 AND ARCH.FCN_ID_ESTATUS_ARCHIVO = 3 ),NO_CONSILDS_IN as( SELECT COUNT(CON.FTN_REF_SUBCTA_secure)    as COUNT_RSA FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO ARCHFOL     ON    CON.FTC_FOLIO = ARCHFOL.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO ARCH             ON    ARCHFOL.FTN_ID_ARCHIVO = ARCH.FTN_ID_ARCHIVO WHERE CON.FTN_ORIGEN_APORTACION IN (287) AND CON.FTN_REF_SUBCTA_secure     != 57822 AND CON.FTN_REG_CONCILIADO         = 0 AND CON.FTN_MOV_GENERADO         = 0 AND ARCH.FCN_ID_ESTATUS_ARCHIVO = 3 ),CONSILDS_NOTIN as( SELECT COUNT(CON.FTN_REF_SUBCTA_secure)    as COUNT_RSA FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO ARCHFOL     ON    CON.FTC_FOLIO = ARCHFOL.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO ARCH             ON    ARCHFOL.FTN_ID_ARCHIVO = ARCH.FTN_ID_ARCHIVO WHERE CON.FTN_ORIGEN_APORTACION         NOT IN (288,292,291,845,844) AND CON.FTN_REF_SUBCTA_secure     != 57822 AND CON.FTN_REG_CONCILIADO         = 1 AND CON.FTN_MOV_GENERADO         = 0 AND ARCH.FCN_ID_ESTATUS_ARCHIVO = 3 ),NO_CONSILDS_NOTIN as( SELECT COUNT(CON.FTN_REF_SUBCTA_secure)    as COUNT_RSA FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO ARCHFOL     ON    CON.FTC_FOLIO = ARCHFOL.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO ARCH             ON    ARCHFOL.FTN_ID_ARCHIVO = ARCH.FTN_ID_ARCHIVO WHERE CON.FTN_ORIGEN_APORTACION         NOT IN (288,292,291,845,844) AND CON.FTN_REF_SUBCTA_secure     != 57822 AND CON.FTN_REG_CONCILIADO         = 0 AND CON.FTN_MOV_GENERADO         = 0 AND ARCH.FCN_ID_ESTATUS_ARCHIVO = 3 ),CONSILDS_NOTIN_BNCO as( SELECT COUNT(CON.FTN_REF_SUBCTA_secure)    as COUNT_RSA FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TLAFOAVOL_SUM_CONCILIACION SUMCON     ON    CON.FTC_FOLIO = SUMCON.FTC_FOLIO LEFT JOIN TTAFOAVOL_BANCOS BANCO                 ON    SUMCON.FTN_IMPORTE = BANCO.FTN_IMPORTE AND CON.FTC_FOLIO = BANCO.FTC_FOLIO_REL INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO ARCHFOL         ON    CON.FTC_FOLIO = ARCHFOL.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO ARCH                 ON    ARCHFOL.FTN_ID_ARCHIVO = ARCH.FTN_ID_ARCHIVO WHERE CON.FTN_ORIGEN_APORTACION         NOT IN (287,288,291,844,845,292) AND CON.FTN_REF_SUBCTA_secure     != 57822 AND CON.FTN_REG_CONCILIADO         = 1 AND CON.FTN_MOV_GENERADO         = 0 AND ARCH.FCN_ID_ESTATUS_ARCHIVO = 3 ),NO_CONSILDS_NOTIN_BNCO as( SELECT COUNT(CON.FTN_REF_SUBCTA_secure)    as COUNT_RSA FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TLAFOAVOL_SUM_CONCILIACION SUMCON     ON    CON.FTC_FOLIO = SUMCON.FTC_FOLIO LEFT JOIN TTAFOAVOL_BANCOS BANCO                 ON    SUMCON.FTN_IMPORTE = BANCO.FTN_IMPORTE AND CON.FTC_FOLIO = BANCO.FTC_FOLIO_REL INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO ARCHFOL         ON    CON.FTC_FOLIO = ARCHFOL.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO ARCH                 ON    ARCHFOL.FTN_ID_ARCHIVO = ARCH.FTN_ID_ARCHIVO WHERE CON.FTN_ORIGEN_APORTACION         NOT IN (287,288,291,844,845,292) AND CON.FTN_REF_SUBCTA_secure     != 57822 AND CON.FTN_REG_CONCILIADO         = 0 AND CON.FTN_MOV_GENERADO         = 0 AND ARCH.FCN_ID_ESTATUS_ARCHIVO = 3 ),NO_CONSILDS_DIVER as( SELECT COUNT(DIVCON.FTN_FONDO_secure)    as COUNT_FA FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON    CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION WHERE CON.FTN_ORIGEN_APORTACION     IN (288,292,291,845,844) AND CON.FTN_REG_CONCILIADO     = 0 AND CON.FTN_MOV_GENERADO     = 0 ),CONSILDS_DIVER as( SELECT COUNT(DIVCON.FTN_FONDO_secure)    as COUNT_FA FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON    CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION WHERE CON.FTN_ORIGEN_APORTACION     IN (288,292,291,845,844) AND CON.FTN_REG_CONCILIADO     = 1 AND CON.FTN_MOV_GENERADO     = 0 ) SELECT CATBANCOS.FCC_VALOR AS BANCOS, (CASE CON.FTN_REF_SUBCTA_secure WHEN 170 THEN 1 WHEN 174 THEN 2 WHEN 175 THEN 3 WHEN 172 THEN 4 END) AS ORDEN, CATsecure.FCC_VALOR AS secure, COUNT(CON.FTN_REF_SUBCTA_secure) AS REGISTROS, SUM(CON.FTN_IMPORTE) AS IMPORTE, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN COUNT(CON.FTN_REF_SUBCTA_secure) ELSE 0 END) AS CONCILIADOS, ((select COUNT_RSA from CONSILDS_IN) + (select COUNT_RSA from CONSILDS_NOTIN_BNCO) + ( SELECT COUNT(DIVCON.FTN_FONDO_secure) FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION WHERE CON.FTN_ORIGEN_APORTACION IN (288,291,844,845) AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0 ) + ( SELECT COUNT(DIVCON.FTN_FONDO_secure) FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION WHERE CON.FTN_ORIGEN_APORTACION IN (292) AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0 )) AS TOTAL_CONCILIADOS, (CASE WHEN CON.FTN_REG_CONCILIADO = 0 THEN COUNT(CON.FTN_REF_SUBCTA_secure) ELSE 0 END) AS NO_CONCILIADOS, ((select COUNT_RSA from NO_CONSILDS_IN) + (select COUNT_RSA from NO_CONSILDS_NOTIN_BNCO) + ( SELECT COUNT(DIVCON.FTN_FONDO_secure) FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION WHERE CON.FTN_ORIGEN_APORTACION IN (288,291,844,845) AND CON.FTN_REG_CONCILIADO = 0 AND CON.FTN_MOV_GENERADO = 0 ) + ( SELECT COUNT(DIVCON.FTN_FONDO_secure) FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION WHERE CON.FTN_ORIGEN_APORTACION IN (292) AND CON.FTN_REG_CONCILIADO = 0 AND CON.FTN_MOV_GENERADO = 0 )) AS TOTAL_NO_CONCILIADOS, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN 'CONCILIADO' ELSE 'NO CONCILIADO' END) AS ESTATUS FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO ARCHFOL ON CON.FTC_FOLIO = ARCHFOL.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO ARCH ON ARCHFOL.FTN_ID_ARCHIVO = ARCH.FTN_ID_ARCHIVO INNER JOIN TCCRXGRAL_CAT_CATALOGO CATsecure ON CON.FTN_REF_SUBCTA_secure = CATsecure.FCN_ID_CAT_CATALOGO LEFT JOIN TCCRXGRAL_CAT_CATALOGO CATBANCOS ON CON.FTN_CVE_BANCO = CATBANCOS.FCN_ID_CAT_CATALOGO WHERE CON.FTN_ORIGEN_APORTACION IN (287) AND CON.FTN_REF_SUBCTA_secure != 57822 AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0 AND ARCH.FCN_ID_ESTATUS_ARCHIVO = 3
 AND CON.FTC_FOLIO_CONCILIACION = #{folioConciliacion} 
GROUP BY CON.FTN_CVE_BANCO, CATBANCOS.FCC_VALOR, CON.FTN_REF_SUBCTA_secure, CATsecure.FCC_VALOR, CON.FTN_REG_CONCILIADO UNION SELECT (CATBANCOS.FCC_VALOR) AS BANCOS, (CASE CON.FTN_REF_SUBCTA_secure WHEN 170 THEN 1 WHEN 174 THEN 2 WHEN 175 THEN 3 WHEN 172 THEN 4 END) AS ORDEN, CATsecure.FCC_VALOR AS secure, COUNT(CON.FTN_REF_SUBCTA_secure) AS REGISTROS, SUM(CON.FTN_IMPORTE) AS IMPORTE, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN COUNT(CON.FTN_REF_SUBCTA_secure) ELSE 0 END) AS CONCILIADOS, ((select COUNT_RSA from CONSILDS_IN) + (select COUNT_RSA from CONSILDS_NOTIN_BNCO) + ( SELECT COUNT(DIVCON.FTN_FONDO_secure) FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION WHERE CON.FTN_ORIGEN_APORTACION IN (288,291,844,845) AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0 ) + ( SELECT COUNT(DIVCON.FTN_FONDO_secure) FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION WHERE CON.FTN_ORIGEN_APORTACION IN (292) AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0 )) AS TOTAL_CONCILIADOS, (CASE WHEN CON.FTN_REG_CONCILIADO = 0 THEN COUNT(CON.FTN_REF_SUBCTA_secure) ELSE 0 END) AS NO_CONCILIADOS, ((select COUNT_RSA from NO_CONSILDS_IN) + (select COUNT_RSA from NO_CONSILDS_NOTIN_BNCO) + ( SELECT COUNT(DIVCON.FTN_FONDO_secure) FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION WHERE CON.FTN_ORIGEN_APORTACION IN (288,291,844,845) AND CON.FTN_REG_CONCILIADO = 0 AND CON.FTN_MOV_GENERADO = 0 ) + ( SELECT COUNT(DIVCON.FTN_FONDO_secure) FROM TTAFOAVOL_CONCILIACION CON INNER JOIN TTAFOAVOL_DIVERSIF_CONC DIVCON ON CON.FTN_ID_CONCILIACION = DIVCON.FTN_ID_CONCILIACION WHERE CON.FTN_ORIGEN_APORTACION IN (292) AND CON.FTN_REG_CONCILIADO = 0 AND CON.FTN_MOV_GENERADO = 0 )) AS TOTAL_NO_CONCILIADOS, (CASE WHEN CON.FTN_REG_CONCILIADO = 1 THEN 'CONCILIADO' ELSE 'NO CONCILIADO' END) AS ESTATUS FROM TTAFOAVOL_CONCILIACION CON INNER JOIN ( SELECT DISTINCT FB.FTC_FOLIO, B.FTN_CVE_BANCO FROM TRAFOAVOL_FOLIO_BANCO FB, TTAFOAVOL_BANCOS B WHERE FB.FTN_ID_BANCO = B.FTN_ID_BANCO) BANCO ON CON.FTC_FOLIO = BANCO.FTC_FOLIO INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO ARCHFOL ON CON.FTC_FOLIO = ARCHFOL.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO ARCH ON ARCHFOL.FTN_ID_ARCHIVO = ARCH.FTN_ID_ARCHIVO INNER JOIN TCCRXGRAL_CAT_CATALOGO CATsecure ON CON.FTN_REF_SUBCTA_secure = CATsecure.FCN_ID_CAT_CATALOGO LEFT JOIN TCCRXGRAL_CAT_CATALOGO CATBANCOS ON CON.FTN_CVE_BANCO = CATBANCOS.FCN_ID_CAT_CATALOGO OR BANCO.FTN_CVE_BANCO = CATBANCOS.FCN_ID_CAT_CATALOGO WHERE CON.FTN_ORIGEN_APORTACION NOT IN (287,288,291,844,845,292) AND CON.FTN_REF_SUBCTA_secure != 57822 AND CON.FTN_REG_CONCILIADO = 1 AND CON.FTN_MOV_GENERADO = 0 AND ARCH.FCN_ID_ESTATUS_ARCHIVO = 3
 AND CON.FTC_FOLIO_CONCILIACION = #{folioConciliacion} 
