mapper.select:
SELECT SOL.FTC_CVE_SOLICITUD, SOL.FTC_FOLIO, SOL.FTN_NUM_CTA_INVDUAL, SOL.FTN_ID_LINEA_NEG , CT_LINEG.FCC_VALOR AS FCC_VALOR_LINEA_NEG, SOL.FTN_ID_ORIGEN_SOLICITUD, CT_ORG_SOL.FCC_VALOR AS FCC_VALOR_ORIGEN_SOLI, SOL.FTC_CTA_BANCO, SOL.FTN_ID_TIPO_CTA , CT_TP_CTA.FCC_VALOR AS FCC_VALOR_TIPO_CTA, SOL.FTN_CVE_BANCO, CT_CV_BANCO.FCC_VALOR AS FCC_VALOR_CLAVE_BANCO, SOL.FTC_NOMBRE_CTE, SOL.FTC_AP_PATERNO_CTE, SOL.FTC_AP_MATERNO_CTE, SOL.FTC_CORREO_ELEC, SOL.FTC_CURP, SOL.FTN_NSS, SOL.FTC_RFC_CTE, SOL.FTN_ID_PERIODO, CT_PERIODO.FCC_VALOR AS FCC_VALOR_PERIODO, ( CASE FTN_ID_PERIODO WHEN 492 THEN FTN_FREC_INICIAL ELSE NULL END ) FTN_ID_SEM_FREC_INICIAL, ( CASE FTN_ID_PERIODO WHEN 492 THEN (SELECT FCC_VALOR FROM TCCRXGRAL_CAT_CATALOGO WHERE FCN_ID_CAT_CATALOGO = FTN_FREC_INICIAL) ELSE NULL END ) FCC_VALOR_SEM_FREC_INICIAL, ( CASE FTN_ID_PERIODO WHEN 492 THEN NULL ELSE FTN_FREC_INICIAL END ) FTN_FREC_INICIAL_DIA, ( CASE FTN_ID_PERIODO WHEN 492 THEN FTN_FREC_FINAL ELSE NULL END ) FTN_ID_SEM_FREC_FINAL, ( CASE FTN_ID_PERIODO WHEN 492 THEN (SELECT FCC_VALOR FROM TCCRXGRAL_CAT_CATALOGO WHERE FCN_ID_CAT_CATALOGO = FTN_FREC_FINAL) ELSE NULL END ) FCC_VALOR_SEM_FREC_FINAL, ( CASE FTN_ID_PERIODO WHEN 492 THEN NULL ELSE FTN_FREC_FINAL END ) FTN_FREC_FINAL_DIA, SOL.FTN_ESTATUS_SOLICITUD, CT_EST_SOL.FCC_VALOR AS FCC_VALOR_ESTATUS_SOL, SOL.FTD_FEH_CAPTURA, SOL.FTN_RECHAZO_SOLICITUD, CT_RECH_SOL.FCC_VALOR AS FCC_VALOR_RECHAZO_SOLICITUD, SOL.FTN_IMPORTE, SOL.FTN_INCREMENTALIDAD , SOL.FTN_OPCION_INCREMENTALIDAD , SOL.FTN_ID_PORCENTAJE_INCREMENTO, CT_POR_INC.FCC_VALOR AS FCC_VALOR_PORCENTAJE_INCRE, SOL.FTN_MONTO_INCREMENTO, SOL.FTN_ID_PERIOD_INCREMENTALIDAD, CT_PER_INC.FCC_VALOR AS FCC_VALOR_PERIOD_INCREMENTA, SOL.FTN_MONEDA, CT_MONEDA.FCC_VALOR AS FCC_VALOR_MONEDA, SOL.FTN_CELULAR, SOL.FTD_FEH_CARGO, SOL.FTD_FEH_ESTATUS_SOLICITUD, SOL.FTN_COMPANIA_CELULAR, CT_COM_CEL.FCC_VALOR AS FTN_VALOR_COMPANIA_CELULAR, SOL.FTD_FEH_CRE AS FTD_SOLI_FEH_CRE, SOL.FTC_USU_CRE AS FTC_SOLI_USU_CRE, SOL.FTD_FEH_ACT AS FTD_SOLI_FEH_ACT, SOL.FTC_USU_ACT AS FTC_SOLI_USU_ACT, DIVER_SOL.FTC_CVE_SOLICITUD AS FTC_DIVER_CVE_SOLICITUD, DIVER_SOL.FTC_FOLIO AS FTC_DIVER_FOLIO, DIVER_SOL.FTN_FONDO_secure AS FTN_DIVER_FONDO_secure, CT_FONDO_secure.FCC_VALOR AS FCC_VALOR_DIVER_FONDO_secure, DIVER_SOL.FTN_MONTO AS FTN_DIVER_MONTO, DIVER_SOL.FTN_PORCENTAJE AS FTN_DIVER_PORCENTAJE, DIVER_SOL.FTD_FEH_CRE AS FTD_DIVER_FEH_CRE, DIVER_SOL.FTC_USU_CRE AS FTC_DIVER_USU_CRE, DIVER_SOL.FTD_FEH_ACT AS FTD_DIVER_FEH_ACT, DIVER_SOL.FTC_USU_ACT AS FTC_DIVER_USU_ACT, SOL.FTN_MEDIO_NOTIFICACION, MOV.FCC_VALOR AS FCC_VALOR_MEDIO_NOTIFICACION, SOL.FTC_ORIGEN_SOL_AFOM, SOL.FTC_VIGENCIA_TC, SOL.FTC_ORIGEN_SOL_AFOM, SOL.FTC_VIGENCIA_TC, SOL.FTC_FOLIO_PROCESAR, SOL.FTC_CURP_TUTOR, SOL.FTC_FOLIO_FAMILIA, SOL.FTC_GERENCIA, SOL.FTC_NOMBRE_PROMOTOR, SOL.FTC_REGIONAL, SOL.FTN_ID_TIPO_REGISTRO, SOL.FTC_NOMBRE_TUTOR, SOL.FTC_AP_PATERNO_TUTOR, SOL.FTC_AP_MATERNO_TUTOR FROM TTAFOAVOL_PRE_SOLICITUD SOL LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_LINEG ON CT_LINEG.FCN_ID_CAT_CATALOGO = SOL.FTN_ID_LINEA_NEG LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_ORG_SOL ON CT_ORG_SOL.FCN_ID_CAT_CATALOGO = SOL.FTN_ID_ORIGEN_SOLICITUD LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_TP_CTA ON CT_TP_CTA.FCN_ID_CAT_CATALOGO = SOL.FTN_ID_TIPO_CTA LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_PERIODO ON CT_PERIODO.FCN_ID_CAT_CATALOGO = SOL.FTN_ID_PERIODO LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_COM_CEL ON CT_COM_CEL.FCN_ID_CAT_CATALOGO = SOL.FTN_COMPANIA_CELULAR LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_EST_SOL ON CT_EST_SOL.FCN_ID_CAT_CATALOGO = SOL.FTN_ESTATUS_SOLICITUD LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_RECH_SOL ON CT_RECH_SOL.FCN_ID_CAT_CATALOGO = SOL.FTN_RECHAZO_SOLICITUD LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_POR_INC ON CT_POR_INC.FCN_ID_CAT_CATALOGO = SOL.FTN_ID_PORCENTAJE_INCREMENTO LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_PER_INC ON CT_PER_INC.FCN_ID_CAT_CATALOGO = SOL.FTN_ID_PERIOD_INCREMENTALIDAD LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_MONEDA ON CT_MONEDA.FCN_ID_CAT_CATALOGO = SOL.FTN_MONEDA LEFT JOIN TTAFOAVOL_DIVERSIF_PRE_SOLIC DIVER_SOL ON DIVER_SOL.FTC_CVE_SOLICITUD = SOL.FTC_CVE_SOLICITUD LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_FONDO_secure ON CT_FONDO_secure.FCN_ID_CAT_CATALOGO = DIVER_SOL.FTN_FONDO_secure LEFT JOIN TCCRXGRAL_CAT_CATALOGO CT_CV_BANCO ON CT_CV_BANCO.FCN_ID_CAT_CATALOGO = SOL.FTN_CVE_BANCO LEFT JOIN TCCRXGRAL_CAT_CATALOGO MOV ON MOV.FCN_ID_CAT_CATALOGO = SOL.FTN_MEDIO_NOTIFICACION
AND SOL.FTN_NUM_CTA_INVDUAL = #{numCuentaIndividual,jdbcType=DECIMAL}
AND SOL.FTC_FOLIO = #{folio,jdbcType=VARCHAR}
AND SOL.FTC_CVE_SOLICITUD = #{claveSolicitud,jdbcType=VARCHAR}
AND SOL.FTC_FOLIO_PROCESAR = #{folioProcesar,jdbcType=VARCHAR}
AND SOL.FTC_CURP = #{curp,jdbcType=VARCHAR}
AND SOL.FTC_CURP_TUTOR = #{curpTutor,jdbcType=VARCHAR}
AND FTC_CTA_BANCO =#{cuentaBanco,jdbcType=VARCHAR}
AND SOL.FTN_ESTATUS_SOLICITUD IN
#{solicitud,jdbcType=DECIMAL}

mapper.update:
